import React, { Component } from 'react';

class Form extends Component {

  state = {
    name: '',
    image: '',
    years_active: '',
    genre: '',
    concert_dates: ''

  }

  handleSubmit = (e) => {
    e.preventDefault()
    // console.log(this.state)
    let newConcert = {
      name: this.state.name,
      image: this.state.image,
      years_active: this.state.years_active,
      genre: this.state.genre,
      concert_dates: this.state.concert_dates
    }
    // console.log(newConcert)
    let reqPack = {
      headers: {"Content-Type":"application/json"},
      method: 'POST',
      body: JSON.stringify(newConcert)
    }
    // console.log(reqPack)
    fetch('http://localhost:3000/toys', reqPack)
    .then(r => r.json())
    .then(toyData => this.props.addnewConcert(toyData))
    // .then(console.log)
  }

  render() {
    return (
      <div className="container">
        <form onSubmit={this.handleSubmit} className="add-toy-form">
          <h3>Create a toy!</h3>
          <input onChange={(e)=>this.setState({name: e.target.value})} type="text" name="name" placeholder="Enter a toy's name..." className="input-text"/>
          <br/>
          <input onChange={(e)=>this.setState({image: e.target.value})} type="text" name="image" placeholder="Enter a toy's image URL..." className="input-text"/>
          <br/>
          <input type="submit" name="submit" value="Create New Toy" className="submit"/>
        </form>
      </div>
    );
  }

}

export default Form;
